import React from 'react';
import ConversationList from '../ConversationList';
import MessageList from '../MessageList';
import './Messenger.css';
import Toolbar from '../Toolbar';
import ToolbarButton from '../ToolbarButton'
import axios from 'axios';

export default class Messenger extends React.Component {
  constructor(props){
    super(props);


    this.state = {
      user: props.user,

    }
  }

  componentDidMount(){
    console.log(this.props.user);

    let newConversations = "";
    axios({
      method: 'get',
      url: 'http://localhost:8055/api/protected/list-user',
      headers: {Authorization: 'Bearer '+this.props.user.Token}
    }).then(response => {
        console.log(response);
        newConversations = response.data.data.map(result => {
          console.log(result);
          return {
            photo: '#',
            name: `${result.Fullname}`,
            text: '',
            token: this.props.user.to
          };
        });

    });

    var ws = new WebSocket("ws://localhost:9009/chat?token="+this.props.user.token);
    ws.onopen = () => {
      console.log("websocket connected");
    }

    ws.onmessage = (chat) => {

    }
  }

  render(){
        return (
          <div className="messenger">
            { <Toolbar
              title="Messenger"
              leftItems={[
                <ToolbarButton key="cog" icon="ion-ios-cog" />
              ]}
              rightItems={[
                <ToolbarButton key="add" icon="ion-ios-add-circle-outline" />
              ]}
            /> }

            { <Toolbar
              title="Conversation Title"
              rightItems={[
                <ToolbarButton key="info" icon="ion-ios-information-circle-outline" />,
                <ToolbarButton key="video" icon="ion-ios-videocam" />,
                <ToolbarButton key="phone" icon="ion-ios-call" />
              ]}
            /> }

            <div className="scrollable sidebar">
              <ConversationList user={this.state.user}/>
            </div>

            <div className="scrollable content">
              <MessageList user={this.state.user}/>
            </div>
          </div>
        );
      }
}
