import React from 'react';
import Messenger from '../Messenger';


const Welcome = ({user, onSignOut})=> {
  // This is a dumb "stateless" component
  return (
    <div>
      Welcome <strong>{user.username}</strong>!
      <a href="javascript:;" onClick={onSignOut}>Sign out</a>
    </div>
  )
}

class LoginForm extends React.Component {

  async loginAsync(Username, Password){
      let rawReponse = await fetch('localhost:8055/api/public/login',{
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        Username: Username,
        Password: Password
      })

    });
    let content = await rawReponse.json();

    return content;
  }

  handleSignIn(e) {
    e.preventDefault()
    let Username = this.refs.Username.value
    let Password = this.refs.Password.value


   this.loginAsync(Username, Password)
    .then( content => console.log(content));


    this.props.onSignIn(Username, Password)
  }

  render() {
    return (
      <div className="container">
        <form onSubmit={this.handleSignIn.bind(this)}>

              <div className="form-group">
                <label>Username</label>
                <input ref="Username" type="text" className="form-control" id="exampleInputEmail1" placeholder="Enter Username"/>
              </div>

              <div class="form-group">
                <label >Password</label>
                <input ref="Password" type="password" className="form-control" id="exampleInputPassword1" placeholder="Password"/>
              </div>

              <button type="submit" className="btn btn-primary" value="Login">Submit</button>
        </form>
      </div>
    )
  }

}


class App extends React.Component {

  constructor(props) {
    super(props)
    // the initial application state
    this.state = {
      user: null
    }
  }

  // App "actions" (functions that modify state)
  signIn(username, password) {
    // This is where you would call Firebase, an API etc...
    // calling setState will re-render the entire app (efficiently!)
    this.setState({
      user: {
        username,
        password,
      }
    })
  }

  signOut() {
    // clear out user from state
    this.setState({user: null})
  }

  render() {
    // Here we pass relevant state to our child components
    // as props. Note that functions are passed using `bind` to
    // make sure we keep our scope to App
    return (
      <div>
        { 
          (this.state.user) ? 
            <Messenger />
          :
            <LoginForm 
             onSignIn={this.signIn.bind(this)} 
            />
        }
      </div>
    )

  }

}

export default App;
